  // @Disabled
  @Test
  @ResourceLock(value = Resources.SYSTEM_OUT, mode = ResourceAccessMode.READ_WRITE)
  public void test_main_human_human() throws IOException {
    ByteArrayOutputStream bytes = new ByteArrayOutputStream();
    PrintStream out = new PrintStream(bytes, true);
    // get an InputStream for input.txt file
    InputStream input = getClass().getClassLoader().getResourceAsStream("input14.txt");
    assertNotNull(input);
    InputStream expectedStream = getClass().getClassLoader().getResourceAsStream("output14.txt");
    assertNotNull(expectedStream);
    InputStream oldIn = System.in;
    PrintStream oldOut = System.out;
    try {
      System.setIn(input);
      System.setOut(out);
      App.main(new String[0]);
    } finally {
      System.setIn(oldIn);
      System.setOut(oldOut);
    }
    // read all the data from expectedStream (output.txt)
    String expected = new String(expectedStream.readAllBytes());
    // get the String out of bytes
    String actual = bytes.toString();
    // finally, compare them
    assertEquals(expected, actual);
  }

  @Test
  public void test_read_null_input() {
    ByteArrayOutputStream bytes = new ByteArrayOutputStream();
    InputStream inputStream = getClass().getClassLoader().getResourceAsStream("EOFfile.txt");
    BufferedReader input = new BufferedReader(new InputStreamReader(inputStream));
    Board<Character> theBoard = new BattleShipBoard<Character>(10, 20, 'X');

    assertThrows(EOFException.class, () -> App.readTextPlayer("A", theBoard, input, System.out));
  }

